mapper.select:
select acs.id as id, acs.commit_time_ms as commit_time_ms, (select count(acl.id) from alf_access_control_list acl where acl.acl_change_set = acs.id ) as acl_count from alf_acl_change_set acs
acs.commit_time_ms >= #{fromCommitTimeInclusive}
and acs.id >= #{fromIdInclusive}
and acs.commit_time_ms < #{toCommitTimeExclusive}
and acs.id < #{toIdExclusive}
order by acs.commit_time_ms ASC, acs.id ASC

select acl.acl_change_set as acl_changeset_id, acl.id as acl_id from alf_access_control_list acl
acl.id >= #{fromIdInclusive}
and acl.acl_change_set in
#{item}
order by acl.id ASC

select
    txn.id              as id,
    txn.commit_time_ms  as commit_time_ms,
    (select
         count(node.id)
     from
         alf_node node
     where
         txn.id = node.transaction_id and
         node.type_qname_id != #{deletedTypeQNameId}
    ) as updates,
    (select
         count(node.id)
     from
         alf_node node
     where
         txn.id = node.transaction_id and
         node.type_qname_id = #{deletedTypeQNameId}
    ) as deletes
from
    alf_transaction txn


       txn.commit_time_ms >= #{fromCommitTimeInclusive}


       and txn.id >= #{fromIdInclusive}


       and txn.commit_time_ms < #{toCommitTimeExclusive}


       and txn.id < #{toIdExclusive}


order by txn.commit_time_ms ASC, txn.id ASC

        select
            COALESCE(np.long_value, node.id) as id,
            node.type_qname_id  as type_qname_id,
            node.transaction_id as txn_id,
            node.uuid           as uuid,
            store.protocol      as protocol,
            store.identifier    as identifier
        from
            alf_transaction txn
            join alf_node node on (txn.id = node.transaction_id)
            join alf_store store on (store.id = node.store_id)
            left outer join alf_node_properties np on (np.node_id = node.id and np.qname_id = #{originalIdPropQNameId})



	                txn.id in 

	                    #{item}



	                txn.id >= #{fromTxnId} and txn.id <= #{toTxnId}


	                txn.id <= #{toTxnId}


                    txn.id >= #{fromTxnId}



                and node.id >= #{fromNodeId}


                and node.id <= #{toNodeId}


                and store.protocol = #{storeProtocol}


                and store.identifier = #{storeIdentifier}


                and node.type_qname_id in 

                    #{item}



                and node.type_qname_id not in 

                    #{item}



                and exists (
                    select *
                    from alf_node_aspects aspects
                    where
                    aspects.node_id = node.id
                    and aspects.qname_id in 

                        #{item}

                )


                and not exists (
                    select *
                    from alf_node_aspects aspects
                    where
                    aspects.node_id = node.id
                    and aspects.qname_id in 

                        #{item}

                )


        order by node.id ASC
